import React from 'react';
import Chart from '../Chart/Chart';

const ExpensesChart = props =>{
    const chartDataPoints=[
        {label: 'Jan', value: 0},
        {label: 'Feb', value: 0},
        {label: 'Mar', value: 0},
        {label: 'Apr', value: 0},
        {label: 'May', value: 0},
        {label: 'Jun', value: 0},
        {label: 'Jul', value: 0},
        {label: 'Aug', value: 0},
        {label: 'Sep', value: 0},
        {label: 'Oct', value: 0},
        {label: 'Nov', value: 0},
        {label: 'Dec', value: 0},
    ];

    for(const expense of props.expenses){             //Instead of in, of would come as props.expenses is an array, not an object.
        const expenseMonth = expense.date.getMonth(); //Starts with index 0, i.e. Jan=0.
        chartDataPoints[expenseMonth].value +=expense.amount;
    }
    return <Chart dataPoints={chartDataPoints}/>;
};

export default ExpensesChart;
